import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { NavbarService } from '../navbar.service';

@Component({
  selector: 'app-gettask',
  templateUrl: './gettask.component.html',
  styleUrls: ['./gettask.component.css']
})
export class GettaskComponent implements OnInit {

  route: string | undefined;

  taskStatus: any;

  public TaskData: any = [];

  todo: boolean = false;
  doing: boolean = false;
  done: boolean = false;
  id: any;

  statusData: any;

  constructor(private router: Router, private http: HttpClient, public nav: NavbarService) {
    this.route = router.url;
  }

  ngOnInit(): void {
    this.nav.hide();
    this.loadData();
  }

  loadData() {
    this.http.get("http://localhost:8080/api/v1/task/gettask").subscribe((resultData: any) => {
      this.TaskData = resultData;
      console.log("Task Details : " + this.TaskData.id);
    });
  }

  openTask(st: string) {
    this.statusData = st;

    // Set the current date and time for startTime and endTime
    const currentDate = new Date();
    const currentDateTime = currentDate.toISOString(); // ISO string format: '2024-11-28T12:34:56.789Z'

    // You can pass these values to the backend when adding or updating the task.
    const newTask = {
      status: st,
      startTime: currentDateTime,
      endTime: currentDateTime // Or you can leave it as "Not Set" for now
    };

    console.log("New Task Details: ", newTask); // Just for debugging

    // Navigate to Add Task Page and pass the current date as the start time
    this.router.navigate(['/addtask', st], { state: { task: newTask } });
  }
  taskId(id: any) {
    this.id = id;
    this.router.navigate(['/updatetask', id]);
    console.log("Task ID - " + id);
  }


