import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  userName: string = "";
  userCity: string = "";
  userEmail: string = "";
  userPassword: string = "";

  userId: any = 0;

  route: string | undefined;

  constructor(private http: HttpClient, private router: Router) {
    this.route = router.url;
  }

  //Save Method to Storing User Details into Database
  save() {
    if (this.userName === "" || this.userCity === "" || this.userEmail === "" || this.userPassword === "") {
      alert("Please Enter Valid Input !!");
    }
    else if (this.userPassword.length < 6) {
      alert("Password Length must be Greater than 6 !!! ");
    }
    else {
      let bodyData = {
        "name": this.userName,
        "email": this.userEmail,
        "city": this.userCity,
        "password": this.userPassword
      };
      //Fetching User Details from UI and Sending to the API using HTTP Post Method
      this.http.post("http://localhost:8080/api/v1/task/saveuser", bodyData, { responseType: 'text' }).subscribe((resultData: any) => {
        console.log(resultData);
        alert("User Registered Successfully");
        this.userName = "";
        this.userCity = "";
        this.userPassword = "";
        this.userId = resultData;

        this.router.navigate(['/gettask']);
      });
    }
  }
}

<section class="vh-100 bg-image"
  style="background-image: url('https://mdbcdn.b-cdn.net/img/Photos/new-templates/search-box/img4.webp');">
  <div class="mask d-flex align-items-center h-100 gradient-custom-3">
    <div class="container h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-12 col-md-9 col-lg-7 col-xl-6">
          <div class="card" style="border-radius: 15px;">
            <div class="card-body p-5">
              <h2 class="text-uppercase text-center mb-5">Create an account</h2>

              <form>

                <div class="form-outline mb-4">
                  <input type="text" id="form3Example1cg" class="form-control form-control-lg" placeholder="Your Name"
                      name="name" [(ngModel)]="userName"/>
                </div>

                <div class="form-outline mb-4">
                  <input type="email" id="form3Example3cg" class="form-control form-control-lg" placeholder="Your Email"
                  name="email" [(ngModel)]="userEmail"/>
                </div>

                <div class="form-outline mb-4">
                  <input type="text" id="form3Example3cg" class="form-control form-control-lg" placeholder="Your City"
                  name="city" [(ngModel)]="userCity"/>
                </div>

                <div class="form-outline mb-4">
                  <input type="password" id="form3Example4cdg" class="form-control form-control-lg" placeholder="Enter Password"
                  name="password" [(ngModel)]="userPassword"/>
                </div>

                <div class="d-flex justify-content-center">
                  <button type="button"
                    class="btn btn-success btn-block btn-lg gradient-custom-4 text-body" (click)="save()">Register</button>
                </div>

                <p class="text-center text-muted mt-5 mb-0">Have already an account? <a routerLink="/login"
                    class="fw-bold text-body" routerLinkActive="active">
                    <u>Login here</u></a></p>

              </form>

            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
